const loadImage = (image: HTMLImageElement) =>
  new Promise<HTMLImageElement>((resolve) => {
    image.onload = () => resolve(image);
  });

const loadOneImage = async (url: string) => {
  const image = new Image();
  image.src = url;
  return loadImage(image).then(data => {
    return data;
  });
};

